import React, { useState } from "react";

export default function BidForm() {
  const [projectName, setProjectName] = useState("");
  const [contractor, setContractor] = useState("");
  const [bidType, setBidType] = useState("");
  const [valueRange, setValueRange] = useState("");
  const [status, setStatus] = useState("");
  const [emails, setEmails] = useState([{ subject: "", body: "" }]);
  const [message, setMessage] = useState("");

  // Handle email inputs change
  const handleEmailChange = (index, field, value) => {
    const newEmails = [...emails];
    newEmails[index][field] = value;
    setEmails(newEmails);
  };

  // Add new email field
  const addEmail = () => {
    setEmails([...emails, { subject: "", body: "" }]);
  };

  // Remove email field
  const removeEmail = (index) => {
    const newEmails = emails.filter((_, i) => i !== index);
    setEmails(newEmails);
  };

  // Submit form data
  const handleSubmit = async (e) => {
    e.preventDefault();

    const bidData = {
      project_name: projectName,
      contractor,
      bid_type: bidType,
      value_range: valueRange,
      status,
      emails,
    };

    try {
      const response = await fetch("http://127.0.0.1:8000/bids/", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(bidData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        setMessage("Failed to create bid: " + JSON.stringify(errorData));
        return;
      }

      const data = await response.json();
      setMessage("Bid created successfully! ID: " + data.id);
      // Clear form
      setProjectName("");
      setContractor("");
      setBidType("");
      setValueRange("");
      setStatus("");
      setEmails([{ subject: "", body: "" }]);
    } catch (error) {
      setMessage("Error: " + error.message);
    }
  };

  return (
    <div>
      <h2>Create New Bid</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Project Name:</label><br />
          <input
            value={projectName}
            onChange={(e) => setProjectName(e.target.value)}
            required
          />
        </div>

        <div>
          <label>Contractor:</label><br />
          <input
            value={contractor}
            onChange={(e) => setContractor(e.target.value)}
            required
          />
        </div>

        <div>
          <label>Bid Type:</label><br />
          <input
            value={bidType}
            onChange={(e) => setBidType(e.target.value)}
            required
          />
        </div>

        <div>
          <label>Value Range:</label><br />
          <input
            value={valueRange}
            onChange={(e) => setValueRange(e.target.value)}
            required
          />
        </div>

        <div>
          <label>Status:</label><br />
          <input
            value={status}
            onChange={(e) => setStatus(e.target.value)}
            required
          />
        </div>

        <h3>Emails</h3>
        {emails.map((email, index) => (
          <div key={index} style={{ marginBottom: "1rem" }}>
            <label>Subject:</label><br />
            <input
              value={email.subject}
              onChange={(e) => handleEmailChange(index, "subject", e.target.value)}
              required
            />
            <br />
            <label>Body:</label><br />
            <textarea
              value={email.body}
              onChange={(e) => handleEmailChange(index, "body", e.target.value)}
              required
            />
            <br />
            {emails.length > 1 && (
              <button type="button" onClick={() => removeEmail(index)}>
                Remove Email
              </button>
            )}
          </div>
        ))}

        <button type="button" onClick={addEmail}>
          Add Another Email
        </button>
        <br /><br />

        <button type="submit">Create Bid</button>
      </form>

      {message && <p>{message}</p>}
    </div>
  );
}
