import React, { useEffect, useState } from 'react';

function App() {
  const [bids, setBids] = useState([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [loading, setLoading] = useState(false);

  // Fetch bids from backend
  const fetchBids = () => {
    fetch('http://127.0.0.1:8000/bids/')
      .then((res) => res.json())
      .then((data) => setBids(data))
      .catch((err) => console.error(err));
  };

  useEffect(() => {
    fetchBids();
  }, []);

  // Handle form submit
  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);

    fetch('http://127.0.0.1:8000/bids/', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ title, description }),
    })
      .then((res) => {
        if (!res.ok) throw new Error('Failed to create bid');
        return res.json();
      })
      .then((newBid) => {
        setBids((prev) => [...prev, newBid]); // Add new bid to list
        setTitle(''); // Clear form
        setDescription('');
      })
      .catch((err) => alert(err.message))
      .finally(() => setLoading(false));
  };

  return (
    <div style={{ maxWidth: 600, margin: 'auto', padding: '20px' }}>
      <img src="/logo.png" alt="Logo" style={{ width: 150, display: 'block', margin: 'auto' }} />
      <h1 style={{ textAlign: 'center' }}>Bid Email Classifier</h1>

      <form onSubmit={handleSubmit} style={{ marginBottom: '30px' }}>
        <h2>Create New Bid</h2>
        <div>
          <input
            type="text"
            placeholder="Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
            style={{ width: '100%', padding: 8, marginBottom: 10 }}
          />
        </div>
        <div>
          <textarea
            placeholder="Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
            style={{ width: '100%', padding: 8, height: 80, marginBottom: 10 }}
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Saving...' : 'Create Bid'}
        </button>
      </form>

      <h2>Bids:</h2>
      <ul>
        {bids.length === 0 ? (
          <li>No bids found</li>
        ) : (
          bids.map((bid) => (
            <li key={bid.id}>
              <strong>{bid.title}</strong>: {bid.description}
            </li>
          ))
        )}
      </ul>
    </div>
  );
}

export default App;
